import React, { Component } from 'react';
import ListadoDesestruturacion from './ListadoDesestruturacion';
import Buscador from './Buscador';
import Crear from './Crear';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listadoPelicula: []
        };
    }

    render() {
        const { listadoPelicula } = this.state;

        return (
            <div className="layout">
                {/* Cabecera */}
                <header className="header">
                    <div className="logo">
                        <div className="play"></div>
                    </div>
                    <h1>MisPelis</h1>
                </header>

                {/* Barra de navegación */}
                <nav className="nav">
                    <ul>
                        <li><a href="/#">Inicio</a></li>
                        <li><a href="/#">Peliculas</a></li>
                        <li><a href="/#">Blog</a></li>
                        <li><a href="/#">Contacto</a></li>
                    </ul>
                </nav>

                {/* Contenido principal */}
                <section id="content" className="content">
                    <ListadoDesestruturacion
                        listadoPelicula={listadoPelicula}
                        setListadoPelicula={this.setListadoPelicula} // Asignación de función de actualización
                    />
                    {/* <ListadoProps listadoPelicula={listadoPelicula} setListadoPelicula={this.setListadoPelicula} /> */}
                </section>

                {/* Barra lateral */}
                <aside className="lateral">
                    <Buscador
                        listadoPelicula={listadoPelicula}
                        setListadoPelicula={this.setListadoPelicula} // Asignación de función de actualización
                    />
                    <Crear
                        setListadoPelicula={this.setListadoPelicula} // Asignación de función de actualización
                    />
                </aside>

                {/* Pie de página */}
                <footer className="footer">
                    &copy; Máster en React - <a href="https://victorroblesweb.es">victorroblesweb.es</a>
                </footer>
            </div>
        );
    }

    // Función para actualizar el estado de listadoPelicula
    setListadoPelicula = (peliculas) => {
        this.setState({ listadoPelicula: peliculas });
    }
}

export default App;
