import React, { Component } from 'react';

export default class GuardarEnStorage extends Component {
    constructor(props) {
        super(props);
    }

    guardarEnStorage = (clave, elemento) => {
        // Conseguir los elementos que ya tenemos en LocalStorage
        let elementos = JSON.parse(localStorage.getItem(clave));

        // Comprobar si es un array
        if (Array.isArray(elementos)) {
            // A침adir dentro del array un elemento nuevo
            elementos.push(elemento);
        } else {
            // Crear un array con el nuevo elemento
            elementos = [elemento];
        }

        // Guardar en el localStorage
        localStorage.setItem(clave, JSON.stringify(elementos));

        // Devolver el elemento guardado (opcional, dependiendo de tus necesidades)
        return elemento;
    };

    render() {
        return null; // No se muestra nada en el render, solo l칩gica de clase
    }
}
