import React, { Component } from 'react';
import Buscador from './Componets/Buscador';
import Crear from './Componets/Crear';
import Listado from './Componets/Listado';


class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listadoPelicula: [], // Inicializar listadoPelicula como un array vacío
        };
    }
    setListadoPelicula = (nuevasPeliculas) => {
        this.setState({ listadoPelicula: nuevasPeliculas });
    }

    render() {
        // Acceder a listadoPelicula directamente desde this.state
        const listadoPelicula = this.state.listadoPelicula;

        return (
            <div className="layout">
                {/* Cabecera */}
                <header className="header">
                    <div className="logo">
                        <div className="play"></div>
                    </div>
                    <h1>MisPelis</h1>
                </header>

                {/* Barra de navegación */}
                <nav className="nav">
                    <ul>
                        <li><a href="/#">Inicio</a></li>
                        <li><a href="/#">Peliculas</a></li>
                        <li><a href="/#">Blog</a></li>
                        <li><a href="/#">Contacto</a></li>
                    </ul>
                </nav>

                {/* Contenido principal */}
                <section id="content" className="content">
                    {/* Pasar listadoPelicula como prop */}
                    <Listado
                        listadoPelicula={this.state.listadoPelicula}
                        setListadoPelicula={this.setListadoPelicula}
                    />
                </section>

                {/* Barra lateral */}
                <aside className="lateral">
                    {/* Pasar listadoPelicula como prop */}
                    <Buscador
                         listadoPelicula={this.state.listadoPelicula}
                         setListadoPelicula={this.setListadoPelicula}
                    />
                    {/* Pasar setListadoPelicula como prop */}
                    <Crear
                        setListadoPelicula={this.setListadoPelicula}
                    />
                </aside>

                {/* Pie de página */}
                <footer className="footer">
                    &copy; Máster en React - <a href="https://victorroblesweb.es">victorroblesweb.es</a>
                </footer>
            </div>
        );
    }
}
export default App;
