class App extends Component {
  constructor(props) {
      super(props);
      this.state = {
          listadoPelicula: []
      };
  }

  render() {
      return (
          <div className="layout">
              {/* Cabecera */}
              <header className="header">
                  <div className="logo">
                      <div className="play"></div>
                  </div>
                  <h1>MisPelis</h1>
              </header>

              {/* Barra de navegación */}
              <nav className="nav">
                  <ul>
                      <li><a href="/#">Inicio</a></li>
                      <li><a href="/#">Peliculas</a></li>
                      <li><a href="/#">Blog</a></li>
                      <li><a href="/#">Contacto</a></li>
                  </ul>
              </nav>

              {/* Contenido principal */}
              <section id="content" className="content">
                  {/* Aquí se pasan los props al componente ListadoDesestruturacion */}
                  <ListadoDesestruturacion 
                      listadoPelicula={this.state.listadoPelicula}
                      setListadoPelicula={peliculas => this.setState({ listadoPelicula: peliculas })}
                  />
                  {/* <ListadoProps listadoPelicula={this.state.listadoPelicula} setListadoPelicula={peliculas => this.setState({ listadoPelicula: peliculas })} /> */}
              </section>

              {/* Barra lateral */}
              <aside className="lateral">
                  {/* Aquí se pasan los props al componente Buscador y Crear */}
                  <Buscador 
                      listadoPelicula={this.state.listadoPelicula}
                      setListadoPelicula={peliculas => this.setState({ listadoPelicula: peliculas })}
                  />
                  <Crear setListadoPelicula={peliculas => this.setState({ listadoPelicula: peliculas })} />
              </aside>

              {/* Pie de página */}
              <footer className="footer">
                  &copy; Máster en React - <a href="https://victorroblesweb.es">victorroblesweb.es</a>
              </footer>
          </div>
      );
  }
}

export default App;
