import React, { Component } from 'react'
import GuardarEnStorageGenerico from '../helpers/GuardarEnStorage'; // Asegúrate de importar desde el archivo correcto

export default class Crear extends Component {
    constructor(props) {
        super(props);
        this.state = {
            tituloComponente: "Añadir película",
            pelicula: {
                titulo: '',
                descripcion: ''
            },
            errores: {
                titulo: '',
                descripcion: ''
            }
        };
        this.guardarHelper = new GuardarEnStorageGenerico(); // Crear instancia de GuardarEnStorageGenerico
    }

    // Función para manejar el envío del formulario
    conseguirDatosForm = (e) => {
        e.preventDefault();

        // Obtener datos del formulario
        let target = e.target;
        let titulo = target.titulo.value;
        let descripcion = target.descripcion.value;

        // Validar campos
        let erroresTemp = this.validarCampos({ titulo, descripcion });

        if (Object.keys(erroresTemp).length > 0) {
            this.setState({ errores: erroresTemp });
            return;
        }

        // Crear objeto de la película a guardar
        let pelicula = {
            id: new Date().getTime(),
            titulo,
            descripcion
        };

        // Actualizar estado con la nueva película
        this.setState({
            pelicula,
            errores: {}
        });

        // Actualizar el estado del listado principal
        this.props.setListadoPelicula(elementos => [...elementos, pelicula]);

        // Guardar en el localStorage usando la instancia de GuardarEnStorageGenerico
        this.guardarHelper("generico", pelicula);

        // Limpiar el formulario
        target.reset();
    };

    /**Validar si los campos vienen vacíos */
    validarCampos = (campos) => {
        let erroresTemp = {};

        if (campos.titulo.trim() === "") {
            erroresTemp.titulo = 'El título es obligatorio.';
        }
        if (campos.descripcion.trim() === "") {
            erroresTemp.descripcion = 'La descripción es obligatoria.';
        }

        return erroresTemp;
    };

    render() {
        const { tituloComponente, pelicula, errores } = this.state;

        return (
            <div className="add">
                <h3 className="title">{tituloComponente}</h3>
                <strong>
                    {/* Si existe título y descripción, muestra la etiqueta */}
                    {(pelicula.titulo && pelicula.descripcion) && `Has creado la película: ${pelicula.titulo}`}
                </strong>

                <form onSubmit={this.conseguirDatosForm}>
                    <input type="text"
                        name="titulo"
                        id="title"
                        placeholder="Título" />
                    {errores.titulo && <p style={{ color: 'red' }}>{errores.titulo}</p>}

                    <textarea id="description"
                        name="descripcion"
                        placeholder="Descripción"></textarea>
                    {errores.descripcion && <p style={{ color: 'red' }}>{errores.descripcion}</p>}

                    <input type="submit" id="save" value="Guardar" />
                </form>
            </div>
        );
    }
}
